<?php

for ($i = 0; $i < 100000; $i++) {
    $number = sprintf('%07d', $i);
    // if ($i == 1) {
        // exit;
    // }
    `convert pictures/template.jpg -annotate +0+9 $number pictures/$i.jpg`;
    file_put_contents("pictures/$i.b64",
        base64_encode(file_get_contents("pictures/$i.jpg")));
    // echo "$number\n";
}
